mapper.select:
select tmc.main_category_name from tb_company_info tci join tb_service_info tsi on tci.company_id = tsi.company_id left join tb_table_meta_info ttmi on tsi.service_id = ttmi.service_id left join tb_main_category tmc on ttmi.main_category_id = tmc.main_category_id where 1=1 and tmc.main_category_name is not null and tsi.service_name = trim(#{serviceName})
group by tmc.main_category_name order by count(*) desc limit #{limit}
group by tmc.main_category_name order by count(*) desc

select tsc.sub_category_name from tb_company_info tci join tb_service_info tsi on tci.company_id = tsi.company_id left join tb_table_meta_info ttmi on tsi.service_id = ttmi.service_id left join tb_main_category tmc on ttmi.main_category_id = tmc.main_category_id left join tb_sub_category tsc on ttmi.sub_category_id = tsc.sub_category_id where 1=1 and tsc.sub_category_name is not null and tsi.service_name = trim(#{serviceName}) and tmc.main_category_name = trim(#{mainCategoryName})
group by tsc.sub_category_name order by count(*) desc limit #{limit}
group by tsc.sub_category_name order by count(*) desc
